INSERT INTO `accessories` (`id`, `title`, `description`, `img`) VALUES
(1, 'Оперативная память', 'Оперативная память — в большинстве случаев энергозависимая часть системы компьютерной памяти, в которой во время работы компьютера хранится выполняемый машинный код, а также входные, выходные и промежуточные данные, обрабатываемые процессором', 3),
(2, 'Видеокарта', 'Видеокарта, также называемая видеоадаптером — это устройство, которое управляет выводом графики в компьютере. Процессор вычисляет данные, передает их на видеокарту и преобразует данные таким образом, чтобы монитор или проектор могли воспроизводить все в виде изображения.', 3),
(3, 'Процессор', 'Центральный процессор — электронный блок либо интегральная схема, исполняющая машинные инструкции, главная часть аппаратного обеспечения компьютера или программируемого логического контроллера. Иногда этот компонент называют просто процессором', 2),
(4, 'Блок питания', 'Встроенный источник электропитания компьютера — устройство, предназначенное для преобразования напряжения переменного тока от сети в напряжение постоянного тока с целью питания компьютера или компьютер-сервера.', 2),
(5, 'Охлаждение компьютера', 'Система охлаждения компьютера — набор средств для отвода тепла от нагревающихся в процессе работы компьютерных компонентов.', 3),
(6, 'Материнская плата', 'Материнская плата - это основная системная плата компьютера, имеющая разъёмы для установки дополнительных плат расширения и служащая механической основой всей электронной схемы компьютера. Благодаря материнской плате обеспечивается полное взаимодействие компонентов компьютерной системы.', 3),
(7, 'Твердотельный накопитель', 'Твердотельный накопитель(Ssd) - это носитель данных, в котором, в отличие от жесткого диска, используется энергонезависимая (флеш) память для хранения данных и доступа к ним. Другими словами, в них нет движущихся механических частей, и это является очень важным фактором.', 1),
(8, 'Корпус компьютера', 'Корпус компьютера - физически представляет собой базовую несущую конструкцию, которая предназначена для последующего наполнения аппаратным обеспечением с целью создания компьютера.', 4);

